import './App.css';
import { Col, Container, Form } from 'react-bootstrap';
import { useState } from 'react';

function App() {

  // Menu pizze ordinabili
  const pizze = [
    { name: 'Margherita', price: 15 },
    { name: 'Marinara', price: 12 },
    { name: 'Capricciosa', price: 20 },
    { name: 'Diavola', price: 18 },
    { name: 'Bufala', price: 18 },
  ]

  // Lista Clienti al tavolo
  const [customers, setCustomers] = useState([]);


  // Ordinazione effettuata
  const [order, setOrder] = useState({
    customers: customers,
    date: new Date(),
  })



  return (
    <>
      <Container fluid className='p-5 bg-secondary' style={{ height: '100vh' }}>
        <h1 className='text-center text-light'>Ordinazione pizze al tavolo</h1>


        <Container className='bg-light p-5 shadow rounded'>
          <h2>Ordina le tue pizze!</h2>
          <Form>
            <Form.Group className='row'>
              <Col sm='6'>
                <Form.Label>Nome e Cognome</Form.Label>
                <Form.Control type='text' id='name' />
              </Col>
              <Col sm='6'>
                <Form.Label>Età</Form.Label>
                <Form.Control type='number' id='age' max={120} min={0} />
              </Col>
            </Form.Group>
            <Form.Group className='row'>
              <Col sm='6' className='mt-2'>
                <Form.Check
                  type={'switch'}
                  label={`Cliente con disabilità`}
                  id={`disabilità`}
                />
              </Col>
              <Col sm='6'>
                <Form.Select aria-label="Default select example">
                  {
                    pizze.length > 0 && pizze.map((key,el)=>{
                      return <option key={key}>{el.name} {el.price}€</option>
                    })
                  }
                </Form.Select>
              </Col>
            </Form.Group>
          </Form>
        </Container>

      </Container>

    </>
  );
}

export default App;
